import React, { useState, useEffect, useCallback } from "react";

import NoteListS from "./components/NoteListSarah";
import NoteListF from "./components/NoteListF";
import AddNoteS from "./components/AddNoteSarah";
import AddNoteF from "./components/AddNoteF";
import "./App.css";
import LoginS from "./components/Login";
function App() {
  const [note, setNote] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isCorrect, setIsCorrect] = useState(false);
  const [error, setError] = useState(null);
  const logindb = [
    { name: "아버지", id: 19687663, pw: 19687663 },
    { name: "어머니", id: 19757663, pw: 19757663 },
    { name: "성재", id: 19957663, pw: 19957663 },
    { name: "은지", id: 19967663, pw: 19967663 },
    { name: "Sarah", id: "sarah0623", pw: "sarah0623" },
  ];

  const fetchNoteHandlerS = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(
        "https://react-http-2887f-default-rtdb.firebaseio.com/notes.json"
      );
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();
      const loadedNotes = [];
      for (const key in data) {
        loadedNotes.push({
          id: key,
          title: data[key].title,
          text: data[key].text,
          date: data[key].date,
        });
      }

      console.log(loadedNotes);
      setNote(loadedNotes);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  const fetchNoteHandlerF = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(
        "https://react-http-2887f-default-rtdb.firebaseio.com/notesf.json"
      );
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();
      const loadedNotes = [];
      for (const key in data) {
        loadedNotes.push({
          id: key,
          title: data[key].title,
          text: data[key].text,
          date: data[key].date,
        });
      }

      console.log(loadedNotes);
      setNote(loadedNotes);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchNoteHandlerS();
    fetchNoteHandlerF();
  }, [fetchNoteHandlerS, fetchNoteHandlerF]);

  const formHandler = ({ id, pw }) => {
    console.log(id);
    // for (const data of logindb) {
    //   console.log(data);
    // }
  };
  formHandler();
  async function addNoteHandlerS(note) {
    const response = await fetch(
      "https://react-http-2887f-default-rtdb.firebaseio.com/notes.json",
      {
        method: "POST",
        body: JSON.stringify(note),
        headers: {
          "Content-Type": "appication/json",
        },
      }
    );
    const data = await response.json();
    console.log(data);
  }

  async function addNoteHandlerF(note) {
    const response = await fetch(
      "https://react-http-2887f-default-rtdb.firebaseio.com/notesf.json",
      {
        method: "POST",
        body: JSON.stringify(note),
        headers: {
          "Content-Type": "appication/json",
        },
      }
    );
    const data = await response.json();
    console.log(data);
  }

  let content = <p>Found no Notes.</p>;

  if (note.length > 0) {
    content = <NoteListS note={note} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <React.Fragment>
      {!isCorrect ? (
        <LoginS form={formHandler}></LoginS>
      ) : (
        <div>
          {" "}
          <section>
            <h2 style={{ color: "white" }}>Log Format</h2>
            {/* <AddNoteS AddNote={addNoteHandler}></AddNoteS> */}
          </section>
          <section>
            {/* <button onClick={fetchNoteHandler}>Update Notes</button> */}
          </section>
          <section>
            <h2 style={{ color: "white" }}>Log</h2>
            {content}
          </section>
        </div>
      )}
    </React.Fragment>
  );
}

export default App;
