import React, { useRef } from "react";

import "./AddNote.css";

function AddNote(props) {
  const titleRef = useRef("");
  const textRef = useRef("");
  const dateRef = useRef("");

  function submitHandler(event) {
    event.preventDefault();

    const movie = {
      title: titleRef.current.value,
      openingText: textRef.current.value,
      releaseDate: dateRef.current.value,
    };

    props.onAddMovie(movie);
  }

  return (
    <form onSubmit={submitHandler}>
      <div className="control">
        <label htmlFor="title">Title</label>
        <input
          type="text"
          id="title"
          ref={titleRef}
          placeholder="Log the title here..!"
        />
      </div>
      <div className="control">
        <label htmlFor="text-log">Text Log</label>
        <textarea rows="5" id="text-log" ref={textRef}></textarea>
      </div>
      <div className="control">
        <label htmlFor="date">Date</label>
        <input type="text" id="date" ref={dateRef} />
      </div>
      <button>Add Movie</button>
    </form>
  );
}

export default AddNote;
