import { Grid, Paper, Avatar, Typography } from "@mui/material";
import LockIcon from "@mui/icons-material/Lock";
import { TextField } from "@mui/material";
import { Button } from "@mui/material";
import { Link } from "@mui/material";
import { useEffect, useState } from "react";
import {
  paperStyle,
  avatarStyle,
  textStyle,
  btnStyle,
} from "./components/loginStyle.js";

const Login = (props) => {
  //states
  const [id, setId] = useState("");
  const navigate = useNavigate();
  const [password, setPassword] = useState("");
  const [formIsValid, setFormIsValid] = useState(false);
  const dispatch = useDispatch();
  const isAuth = useSelector((select) => select.isAuth);

  const loginHandlerAxios = async (email, password) => {
    try {
      // the http request login
      let userData = await userLoginFetch({ email, password });
      let access_token = userData["access_token"];
      if (access_token) {
        saveJwtToLocal(access_token);
      }
      // set add a single action for updating these 3
      dispatch(usersActions.updateId(id));
      dispatch(usersActions.updatePassword(access_token));
      dispatch(usersActions.updateIsauth(true));
      //navigate to other page
      navigate("./home");
    } catch (e) {
      alert("login failed");
    }
  };

  const loginHandlerFetch = async (email, password) => {
    try {
      // the http request login
      let response = await userLoginFetch({ email, password });
      console.log(response);
      //Unsuccessful case
      //Error Handling
      if (!response.ok) {
        if (response.status === 401) {
          throw Error("Not authorized");
        } else if (response.status === 403) {
          throw Error("403");
        }
        throw new Error(response);
      }
      // successful case
      // saving accesstoken to local storage
      let access_token = response["access_token"];
      if (access_token) {
        saveJwtToLocal(access_token);
      }
      // set add a single action for updating these 3
      dispatch(usersActions.updateId(id));
      dispatch(usersActions.updatePassword(access_token));
      dispatch(usersActions.updateIsauth(true));
      //navigate to other page
      navigate("./home");
    } catch (e) {
      alert(e);
    }
  };

  useEffect(() => {
    //Checking disabled whenever user type something
    setFormIsValid(id.includes("@") && password.trim().length >= 4);
    if (isAuth) {
      navigate("/home");
    }
  }, [isAuth, id, password]);

  //function
  const idHandler = (e) => {
    setId(e.target.value);
  };

  const passwordHandler = (e) => {
    setPassword(e.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    if (formIsValid) {
      loginHandlerFetch(id, password);
    } else {
      alert("Email invalid or password length < 4");
    }
    //Sending props to parents app
  };

  return (
    <Grid align="center">
      <Paper elevation={10} style={paperStyle}>
        <Avatar style={avatarStyle}>
          <LockIcon></LockIcon>
        </Avatar>
        <h2>Sign In</h2>
        <TextField
          style={textStyle}
          label="Username"
          placeholder="Enter Username"
          fullWidth
          required
          onChange={idHandler}
        ></TextField>
        <TextField
          style={textStyle}
          label="Password"
          placeholder="Enter Password"
          fullWidth
          required
          type="password"
          onChange={passwordHandler}
        ></TextField>
        <Button
          type="submit"
          color="primary"
          fullWidth
          variant="contained"
          style={btnStyle}
          onClick={submitHandler}
          disabled={!formIsValid}
        >
          Sign In
        </Button>
        <Typography align="left">
          <Link href="https://www.naver.com" target="_blank;">
            Forgot Password?
          </Link>
        </Typography>
        <Typography align="left">
          Do you have an account?
          <Link href="https://www.naver.com" target="_blank;">
            Sign Up
          </Link>
        </Typography>
      </Paper>
    </Grid>
  );
};

export default Login;
