import React, { useState, useEffect, useCallback } from "react";
import {
  fetchNoteHandlerEn,
  fetchNoteHandlerKr,
  addNoteHandlerEn,
  addNoteHandlerKr,
} from "../../Service/ApiService";
import AddNoteKr from "./AddNoteKr";
import { useSelector, useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { usersActions } from "../../store/userSlice";

const MessageLogFormKr = () => {
  const reduxUser = useSelector((state) => state.user);
  const dispatch = useDispatch();
  const [note, setNote] = useState([]);
  let navigate = useNavigate();

  const logoutHandler = () => {
    dispatch(usersActions.updateIdToken(null));
    dispatch(usersActions.updateIsLoggedIn(false));
    dispatch(usersActions.updateUser(null));
    localStorage.removeItem("token");
    localStorage.removeItem("isLoggedIn");
    localStorage.removeItem("user");
    navigate("/login");
  };

  const fetchHandler = useCallback(async () => {
    const temp = fetchNoteHandlerKr;
    setNote(temp);
  }, []);

  useEffect(() => {
    fetchHandler();
  }, [fetchHandler]);

