import React from "react";
import {
  Drawer,
  ListItem,
  List,
  ListItemIcon,
  ListItemText,
} from "@mui/material";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";

const DrawerComponent = ({ openDrawer, setOpenDrawer }) => {
  const reduxIsLoggedIn = useSelector((state) => state.isLoggedIn);
  const reduxUser = useSelector((state) => state.user);
  let navigate = useNavigate();
  return (
    <Drawer anchor="right" open={openDrawer}>
      {reduxIsLoggedIn ? (
        <List>
          <ListItem
            divider
            button
            onClick={() => {
              if (reduxUser === "Hans" || reduxUser === "Sarah") {
                navigate("/hompageen");
              } else if (
                reduxUser === "성재" ||
                reduxUser === "엄마" ||
                reduxUser === "아빠" ||
                reduxUser === "은지"
              ) {
                navigate("/hompagekr");
              }
              setOpenDrawer(false);
            }}
          >
            <ListItemIcon>
              <ListItemText>Home / 홈</ListItemText>
            </ListItemIcon>
          </ListItem>
          <ListItem
            divider
            button
            onClick={() => {
              navigate("/changepassword");
              setOpenDrawer(false);
            }}
          >
            <ListItemIcon>
              <ListItemText>Change Password / 비밀번호 바꾸기</ListItemText>
            </ListItemIcon>
          </ListItem>
          <ListItem
            divider
            button
            onClick={() => {
              if (reduxUser === "Hans" || reduxUser === "Sarah") {
                navigate("/msglogen");
              } else if (
                reduxUser === "성재" ||
                reduxUser === "엄마" ||
                reduxUser === "아빠" ||
                reduxUser === "은지"
              ) {
                navigate("/msglogkr");
              }
              setOpenDrawer(false);
            }}
          >
            <ListItemIcon>
              <ListItemText>Message Log / 메세지 로그</ListItemText>
            </ListItemIcon>
          </ListItem>
          <ListItem
            divider
            button
            onClick={() => {
              navigate("/updateusername");
              setOpenDrawer(false);
            }}
          >
            <ListItemIcon>
              <ListItemText>Update Profile / 계정 정보 업데이트</ListItemText>
            </ListItemIcon>
          </ListItem>
        </List>
      ) : (
        <List>
          <ListItem
            divider
            button
            onClick={() => {
              navigate("/signin");
              setOpenDrawer(false);
            }}
          >
            <ListItemIcon>
              <ListItemText>가입하기 / Register</ListItemText>
            </ListItemIcon>
          </ListItem>
          <ListItem
            divider
            button
            onClick={() => {
              navigate("/login");
              setOpenDrawer(false);
            }}
          >
            <ListItemIcon>
              <ListItemText>로그인/ Log In</ListItemText>
            </ListItemIcon>
          </ListItem>
        </List>
      )}
    </Drawer>
  );
};

export default DrawerComponent;
