import React, { useRef } from "react";
import "./AddNote.css";

const Login = (props) => {
  const pwRef = useRef("");

  const pwHandler = () => {
    console.log(pwRef.current.value);
    props.pwHandler(pwRef.current.value);
  };
  return (
    <div className="control">
      <input type="text" id="pw" ref={pwRef} placeholder="Password" />
      <button type="submit" onSubmit={pwHandler}>
        Submit
      </button>
    </div>
  );
};

export default Login;
