import {
  Toolbar,
  IconButton,
  Typography,
  Badge,
  useMediaQuery,
} from "@mui/material";
import React, { useState } from "react";
import AppsIcon from "@mui/icons-material/Apps";
import HomeIcon from "@mui/icons-material/Home";
import { Box } from "@mui/system";
import DrawerComponent from "./DrawerComponent";
import { useTheme } from "@mui/material/styles";
import ListIcon from "@mui/icons-material/List";
import { useSelector, useDispatch } from "react-redux";

const Navbar = () => {
  const [openDrawer, setOpenDrawer] = useState(true);
  const theme = useTheme();
  const matches = useMediaQuery(theme.breakpoints.down("sm"));
  const reduxIsLoggedIn = useSelector((state) => state.isLoggedIn);

  console.log(reduxIsLoggedIn);

  let context;
  const renderNavigation = () => {
    if (matches) {
      context = (
        <DrawerComponent
          openDrawer={openDrawer}
          setOpenDrawer={setOpenDrawer}
        />
      );
    } else if (!matches && reduxIsLoggedIn) {
      context = (
        <Box
          sx={{
            display: "flex",
          }}
        >
          <Typography
            sx={{
              marginRight: "20px",
              cursor: "pointer",
              color: "white",
            }}
          >
            Home / 홈
          </Typography>
          <Typography
            sx={{
              marginRight: "20px",
              cursor: "pointer",
              color: "white",
            }}
          >
            Change Password / 비밀번호 바꾸기
          </Typography>
          <Typography
            sx={{
              marginRight: "20px",
              cursor: "pointer",
              color: "white",
            }}
          >
            Message Log / 메세지 로그
          </Typography>
        </Box>
      );
    } else if (!matches && !reduxIsLoggedIn) {
      context = null;
    }
  };
  return (
    <>
      <Box
        elevation={10}
        sx={{
          backgroundColor:
            "linear-gradient(to left, #3399ff 27%, #9999ff 100%);",
        }}
      >
        <Toolbar>
          <Box
            sx={{
              display: "flex",
              justifyContent: "space-between",
              width: "100%",
              alignItems: "center",
              padding: "10px 0px",
            }}
            component="div"
          >
            {/* link */}
            {matches && (
              <DrawerComponent
                openDrawer={openDrawer}
                setOpenDrawer={setOpenDrawer}
              />
            )}
            {reduxIsLoggedIn && !matches ? (
              <Box
                sx={{
                  display: "flex",
                }}
              >
                <Typography
                  sx={{
                    marginRight: "20px",
                    cursor: "pointer",
                    color: "white",
                  }}
                >
                  Home / 홈
                </Typography>
                <Typography
                  sx={{
                    marginRight: "20px",
                    cursor: "pointer",
                    color: "white",
                  }}
                >
                  Change Password / 비밀번호 바꾸기
                </Typography>
                <Typography
                  sx={{
                    marginRight: "20px",
                    cursor: "pointer",
                    color: "white",
                  }}
                >
                  Message Log / 메세지 로그
                </Typography>
              </Box>
            ) : null}
            {/* Button link */}
            <Box
              sx={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
              }}
            >
              {matches ? (
                <IconButton onClick={() => setOpenDrawer(true)}>
                  <ListIcon></ListIcon>
                </IconButton>
              ) : (
                <button>Register / 계정생성</button>
              )}
            </Box>
          </Box>
        </Toolbar>
      </Box>
    </>
  );
};

export default Navbar;
