import React, { useState, useEffect, useCallback } from "react";

import NoteList from "./components/NoteList";
import AddNote from "./components/AddNote";
import "./App.css";
function App() {
  const [note, setNote] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchNoteHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(
        "https://react-http-2887f-default-rtdb.firebaseio.com/note.json"
      );
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();
      const loadedNotes = [];
      for (const key in data) {
        loadedNotes.push({
          id: key,
          title: data[key].title,
          text: data[key].text,
          date: data[key].date,
        });
      }

      console.log(loadedNotes);
      setNote(loadedNotes);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchNoteHandler();
  }, [fetchNoteHandler]);

  async function addNoteHandler(movie) {
    const response = await fetch(
      "https://react-http-2887f-default-rtdb.firebaseio.com/note.json",
      {
        method: "POST",
        body: JSON.stringify(movie),
        headers: {
          "Content-Type": "appication/json",
        },
      }
    );
    const data = await response.json();
    console.log(data);
  }

  let content = <p>Found no Notes.</p>;

  if (note.length > 0) {
    content = <NoteList note={note} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <React.Fragment>
      <section>
        <AddNote AddNote={addNoteHandler}></AddNote>
      </section>
      <section>
        <button onClick={fetchNoteHandler}>Update Notes</button>
      </section>
      <section>{content}</section>
    </React.Fragment>
  );
}

export default App;
