import React, { useState, useEffect, useCallback } from "react";

import NoteListS from "./components/NoteListSarah";
import NoteListF from "./components/NoteListF";
import AddNoteS from "./components/AddNoteSarah";
import AddNoteF from "./components/AddNoteF";
import "./App.css";
import Login from "./components/Login";
function App() {
  const [noteF, setNoteF] = useState([]);
  const [noteS, setNoteS] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isCorrect, setIsCorrect] = useState(false);
  const [mode, setMode] = useState(false);
  const [error, setError] = useState(null);
  const [userInfo, SetUserInfo] = useState("");
  const logindb = [
    { name: "아버지", id: "19687663", pw: "19687663", mode: "F" },
    { name: "어머니", id: "19757663", pw: "19757663", mode: "F" },
    { name: "성재", id: "19957663", pw: "19957663", mode: "F" },
    { name: "은지", id: "19967663", pw: "19967663", mode: "F" },
    { name: "Sarah", id: "sarah0623", pw: "sarah0623", mode: "S" },
    { name: "Hans", id: "hans0623", pw: "hans0623", mode: "S" },
  ];

  const fetchNoteHandlerS = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(
        "https://react-http-2887f-default-rtdb.firebaseio.com/notes.json"
      );
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();
      const loadedNotes = [];
      for (const key in data) {
        loadedNotes.push({
          id: key,
          title: data[key].title,
          text: data[key].text,
          date: data[key].date,
        });
      }

      console.log(loadedNotes);
      setNoteS(loadedNotes);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  const fetchNoteHandlerF = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(
        "https://react-http-2887f-default-rtdb.firebaseio.com/notesf.json"
      );
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();
      const loadedNotes = [];
      for (const key in data) {
        loadedNotes.push({
          id: key,
          title: data[key].title,
          text: data[key].text,
          date: data[key].date,
        });
      }

      console.log(loadedNotes);
      setNoteF(loadedNotes);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchNoteHandlerS();
    fetchNoteHandlerF();
  }, [fetchNoteHandlerS, fetchNoteHandlerF]);

  const formHandler = ({ id, pw }) => {
    console.log(id, pw);
    for (const data of logindb) {
      if (id === data.id && pw === data.pw && id) {
        setIsCorrect(true);
        const temp = data.name;
        SetUserInfo(temp);
        if (data.mode === "F") {
          setMode(true);
        }
      }
    }
  };

  async function addNoteHandlerS(note) {
    const response = await fetch(
      "https://react-http-2887f-default-rtdb.firebaseio.com/notes.json",
      {
        method: "POST",
        body: JSON.stringify(note),
        headers: {
          "Content-Type": "appication/json",
        },
      }
    );
    const data = await response.json();
    console.log(data);
  }

  async function addNoteHandlerF(note) {
    const response = await fetch(
      "https://react-http-2887f-default-rtdb.firebaseio.com/notesf.json",
      {
        method: "POST",
        body: JSON.stringify(note),
        headers: {
          "Content-Type": "appication/json",
        },
      }
    );
    const data = await response.json();
    console.log(data);
  }

  let content = <p color="white">Found no Notes.</p>;

  if (noteF.length > 0 && mode) {
    content = <NoteListF note={noteF} />;
  }

  if (noteS.length > 0 && !mode) {
    content = <NoteListS note={noteS} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <>
      {!isCorrect ? (
        <Login form={formHandler}></Login>
      ) : mode ? (
        <div>
          <section>
            <h3 style={{ color: "white" }}>어서오세요..! {userInfo}</h3>
            <h2 style={{ color: "white" }}>메세지 포맷</h2>
            <AddNoteF AddNote={addNoteHandlerF}></AddNoteF>
          </section>
          <section>
            <button onClick={fetchNoteHandlerF}>Update Notes</button>
          </section>
          <section>
            <h2 style={{ color: "white" }}>로그</h2>
            {content}
          </section>
        </div>
      ) : (
        <div>
          <section>
            <h3 style={{ color: "white" }}>Welcome..! {userInfo}</h3>
            <h3 style={{ color: "white" }}>Log Format</h3>
            <AddNoteS AddNote={addNoteHandlerS}></AddNoteS>
          </section>
          <section>
            <button onClick={fetchNoteHandlerS}>Update Notes</button>
          </section>
          <section>
            <h3 style={{ color: "white" }}>Log</h3>
            {content}
          </section>
        </div>
      )}
    </>
  );
}

export default App;
