import React, { useRef } from "react";

const Login = (props) => {
  const idRef = useRef("");
  const pwRef = useRef("");

  const formHandler = () => {
    props.form({ id: idRef.current.value, pw: pwRef.current.value });
  };

  return (
    <section>
      <h3 style={{ color: "white" }}>
        Input the password to access the Message Log
      </h3>
      <h3 style={{ color: "white" }}>
        로그에 접속 하려면 아이디와 패스워드를 입력해주세요.
      </h3>
      <input
        style={{
          backgroundColor: "transparent",
          backgroundRepeat: "no-repeat",
          borderRadius: "5px",
          padding: "0.2rem",
          border: "1px solid #ccc",
          color: "white",
        }}
        type="text"
        id="id"
        ref={idRef}
        placeholder="id"
      />
      <br></br>
      <br></br>
      <input
        style={{
          backgroundColor: "transparent",
          backgroundRepeat: "no-repeat",
          borderRadius: "5px",
          padding: "0.2rem",
          border: "1px solid #ccc",
          color: "white",
        }}
        type="text"
        id="pw"
        ref={pwRef}
        placeholder="Password"
      />
      <br></br>
      <br></br>
      <button onClick={formHandler}>Submit</button>
    </section>
  );
};

export default Login;
