import React, { useRef } from "react";

const Login = (props) => {
  const pwRef = useRef("");

  const pwHandler = () => {
    props.pw(pwRef.current.value);
  };

  return (
    <section>
      <input
        style={{
          backgroundColor: "transparent",
          backgroundRepeat: "no-repeat",
        }}
        type="text"
        id="pw"
        ref={pwRef}
        placeholder="Password"
      />
      <button style={{ marginLeft: 20 }} onClick={pwHandler}>
        Submit
      </button>
    </section>
  );
};

export default Login;
