const logIn = async (enteredEmail, enteredPassword) => {
  try {
    const data = await fetch(
      "https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyCkLePHqkPxNxPalsSLE_C4CbAaOiaGwNw",
      {
        method: "POST",
        body: JSON.stringify({
          email: enteredEmail,
          password: enteredPassword,
          returnSecureToken: true,
        }),
        headers: {
          "Content-Type": "application/json",
        },
      }
    );
    if (!data.ok) {
      console.log(data);
      let errorMessage = "Authentication failed! \n 로그인에 실패하였습니다.";
      if (data?.error?.message) {
        //equal with if(data && data.error && data.error.message)
        errorMessage = data.error.message;
      }
      throw new Error(errorMessage);
    }
    const json = await data.json();
    console.log(json);
    return json;
  } catch (err) {
    alert(err);
  }
};

const signIn = (enteredEmail, enteredPassword) => {
  fetch(
    "https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyCkLePHqkPxNxPalsSLE_C4CbAaOiaGwNw",
    {
      method: "POST",
      body: JSON.stringify({
        email: enteredEmail,
        password: enteredPassword,
        returnSecureToken: true,
      }),
      headers: {
        "Content-Type": "application/json",
      },
    }
  )
    .then((res) => {
      if (res.ok) {
        return res.json();
      } else {
        return res.json().then((data) => {
          console.log(data);
          let errorMessage = "Authentication failed!";
          if (data?.error?.message) {
            //equal with if(data && data.error && data.error.message)
            errorMessage = data.error.message;
          }
          throw new Error(errorMessage);
        });
      }
    })
    .then((data) => {
      console.log(data);
    })
    .catch((err) => {
      alert(err.message);
    });
};

const changePassword = (enteredNewPassword) => {
  fetch(
    "https://identitytoolkit.googleapis.com/v1/accounts:update?key=AIzaSyCkLePHqkPxNxPalsSLE_C4CbAaOiaGwNw",
    {
      method: "POST",
      body: JSON.stringify({
        // idToken: authCtx.token,
        password: enteredNewPassword,
        returnSecureToken: false,
      }),
      headers: {
        "Content-Type": "application/json",
      },
    }
  )
    .then((res) => {
      if (res.ok) {
        return res.json();
      } else {
        return res.json().then((data) => {
          console.log(data);
          let errorMessage = "Failed to change password";
          if (data?.error?.message) {
            //equal with if(data && data.error && data.error.message)
            errorMessage = data.error.message;
          }
          throw new Error(errorMessage);
        });
      }
    })
    .then((data) => {
      console.log(data);
      alert("Success password..!");
    })
    .catch((err) => {
      alert(err.errorMessage);
    });
};

